<li>	<dl id="searct_location" class="search_location">		<dt class="" value="{{$value}}">{{$text}}</dt>	</dl>	<ul class="search_location_block" id="locations" style="display: none;">		@foreach ($locations as $value => $text)			<li value="{{$value}}"><a href="javascript:;">{{$text}}</a></li>		@endforeach	</ul></li><script>	$("#locations li").on('click', function(){		$("#searct_location dt").text($(this).find('a').text());		$("#searct_location dt").attr('value', $(this).attr('value'));		$(this).parent().hide();	});</script>