<div >	<ul class="pagenum">		<li class="btnleft">			<a href="{!! $url . '&page=' . $previous_page !!}"  title="回上頁">回上頁</a>		</li>		<li class="center">			<?php			$previousPage = 0;			foreach ($show_pages as $page) {				if ($previousPage !== 0 && ($page - $previousPage !== 1)) {				?>				<span>...</span>				<?php				}			?>			<a href="{!! $url . '&page=' . $page !!}" title="第<?php echo $page;?>頁"><?php echo $page;?></a>			<?php				$previousPage = $page;			}			?>		</li>		<li class="btnright">			<a href="{!! $url . '&page=' . $next_page !!}"  title="到下頁">到下頁</a>		</li>	</ul></div>